mapper.insert:


	SELECT LAST_INSERT_ID() AS mem_idx


INSERT INTO
	t_member (
		mem_id
		, mem_nick
		, mem_passwd
		, mem_email
	) VALUES (
		#{mem_id}
		, #{mem_nick}
		, #{mem_passwd}
		, #{mem_email}
	)

mapper.update:
SELECT mem_idx FROM t_member WHERE mem_id = #{mem_id}
UPDATE t_member
mem_nick = ${mem_nick},
mem_passwd = ${mem_passwd},
mem_email = ${mem_email},
WHERE mem_id = #{mem_id}
